// this file is there just for retro compatibility with clients that did not modify their sites yet
// instead of targeting bundle.min.js, they should target loader.js, to do not have cache issues anymore
// content of this file MUST BE the same than loader.js
// difference between them is that loader.js has a 20s max-age set in CDN nginx, while bundle.min.js don't have it

function injectScript() {
  return new Promise((resolve, reject) => {
    const script = document.createElement('script');
    // url will be replaced by Backend at publish time
    script.src = 'TIMESTAMPED_CDN_URL/bundle.min.js';
    script.addEventListener('load', resolve);
    script.addEventListener('error', (e) => reject(e.error));
    document.head.appendChild(script);
  });
}

injectScript()
  .then(() => {
    console.log('Script loaded!');
  })
  .catch((error) => {
    console.error(error);
  });
